import { useState, useEffect } from 'react';

function Company(props) {
    const { company, companies, setCompanies } = props;
    const [expanded, setExpanded] = useState(false);
    const [relatedContacts, setRelatedContacts] = useState([]); // Assuming you want to fetch related contacts

    // Fetch related contacts when the component mounts or company changes
    useEffect(() => {
        const fetchContacts = async () => {
            try {
                const response = await fetch(`http://localhost/api/companies/${company.id}/contacts`);
                const data = await response.json();
                setRelatedContacts(data);
            } catch (error) {
                console.error('Error fetching contacts:', error);
            }
        };

        fetchContacts();
    }, [company.id]); // Only run this effect when company.id changes

    const expandStyle = {
        display: expanded ? 'block' : 'none'
    };

    async function doDelete(e) {
        e.stopPropagation();

        try {
            const response = await fetch(`http://localhost/api/companies/${company.id}`, {
                method: 'DELETE',
            });

            if (response.ok) {
                const newCompanies = companies.filter(c => c.id !== company.id);
                setCompanies(newCompanies);
            } else {
                console.error('Failed to delete company:', response);
                alert('Failed to delete company. Please try again.');
            }
        } catch (error) {
            console.error('Error during deletion:', error);
            alert('An error occurred during deletion. Please try again.');
        }
    }

    return (
        <div key={company.company_id} className='company' onClick={() => setExpanded(!expanded)}>
            <div className='title'>
                <h3>{company.name}</h3>
                <button className='button red' onClick={doDelete}>Delete Company</button>
            </div>

            <div style={expandStyle}>
                <hr />
                <div style={{ textAlign: 'left' }}>
                    <p style={{ fontWeight: 'bold' }}>Company Name: <span style={{ fontWeight: 'normal' }}>{company.name}</span></p>
                    <p style={{ fontWeight: 'bold' }}>Company Address: <span style={{ fontWeight: 'normal' }}>{company.address}</span></p>
                    <p style={{ fontWeight: 'bold' }}>Contact ID: <span style={{ fontWeight: 'normal' }}>{company.contact_id}</span></p>
                </div>

                {/* Render related contacts if any */}
                {relatedContacts.length > 0 && (
                    <div>
                        <h4>Related Contacts:</h4>
                        <ul>
                            {relatedContacts.map(contact => (
                                <li key={contact.id}>{contact.name} - {contact.phone}</li> // Adjust according to your contact structure
                            ))}
                        </ul>
                    </div>
                )}
            </div>
        </div>
    );
}

export default Company;
