import { useState, useEffect } from 'react';

function Company(props) {
    const { company, companies, setCompanies } = props;
    const [expanded, setExpanded] = useState(false);
    const [isEditing, setIsEditing] = useState(false); // State to track if editing mode is on
    const [companyName, setCompanyName] = useState(company.company_name);
    const [companyAddress, setCompanyAddress] = useState(company.company_address);
    const [contactId, setContactId] = useState(company.contact_id);

    const expandStyle = {
        display: expanded ? 'block' : 'none'
    };

    const handleEditToggle = () => {
        setIsEditing(!isEditing);
    };

    async function doDelete(e) {
        e.stopPropagation();
        try {
            const response = await fetch('http://localhost/api/companies/' + company.company_id, {
                method: 'DELETE',
            });

            if (response.ok) {
                const newCompanies = companies.filter(c => c.company_id !== company.company_id);
                setCompanies(newCompanies);
            } else {
                console.error('Failed to delete company:', response);
                alert('Failed to delete company. Please try again.');
            }
        } catch (error) {
            console.error('Error during deletion:', error);
            alert('An error occurred during deletion. Please try again.');
        }
    }

    async function handleUpdate(e) {
        e.preventDefault(); // Prevent default form submission
        try {
            const updatedCompany = {
                company_name: companyName,
                company_address: companyAddress,
                contact_id: contactId,
            };

            const response = await fetch('http://localhost/api/companies/' + company.company_id, {
                method: 'PATCH',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(updatedCompany),
            });

            if (response.ok) {
                const newCompanies = companies.map(c =>
                    c.company_id === company.company_id ? { ...c, ...updatedCompany } : c
                );
                setCompanies(newCompanies);
                setIsEditing(false); // Exit editing mode
            } else {
                const errorMessage = await response.text();
                console.error('Failed to update company:', response, errorMessage);
                alert('Failed to update company: ' + errorMessage);
            }
        } catch (error) {
            console.error('Error during update:', error);
            alert('An error occurred during update. Please try again.');
        }
    }

    return (
        <div key={company.company_id} className='company'>
            <div className='title' onClick={() => setExpanded(!expanded)}>
                <h3>{company.company_name}</h3>
            </div>

            <div style={expandStyle}>
                <hr />
                {isEditing ? (
                    <form className='new-company' onSubmit={handleUpdate}>
                        <div style={{ marginBottom: '20px' }}>
                            <label style={{ 
                                display: 'block', 
                                marginBottom: '5px', 
                                textAlign: 'left',
                                fontSize: '15px' 
                            }}>
                                Company Name:
                            </label>
                            <input 
                                type='text' 
                                placeholder='Enter Company Name' 
                                onChange={(e) => setCompanyName(e.target.value)} 
                                value={companyName} 
                                style={{ 
                                    width: '100%', 
                                    borderRadius: '10px', 
                                    padding: '10px', 
                                    border: '1px solid #ccc', 
                                    fontSize: '15px' 
                                }} 
                            />
                        </div>
                        <div style={{ marginBottom: '20px' }}>
                            <label style={{ 
                                display: 'block', 
                                marginBottom: '5px', 
                                textAlign: 'left',
                                fontSize: '15px' 
                            }}>
                                Company Address:
                            </label>
                            <input 
                                type='text' 
                                placeholder='Enter Company Address' 
                                onChange={(e) => setCompanyAddress(e.target.value)} 
                                value={companyAddress} 
                                style={{ 
                                    width: '100%', 
                                    borderRadius: '10px', 
                                    padding: '10px', 
                                    border: '1px solid #ccc', 
                                    fontSize: '15px' 
                                }} 
                            />
                        </div>
                        <div style={{ marginBottom: '20px' }}>
                            <label style={{ 
                                display: 'block', 
                                marginBottom: '5px', 
                                textAlign: 'left',
                                fontSize: '15px' 
                            }}>
                                Contact ID:
                            </label>
                            <input 
                                type='text' 
                                placeholder='Enter Contact ID' 
                                onChange={(e) => setContactId(e.target.value)} 
                                value={contactId} 
                                style={{ 
                                    width: '100%', 
                                    borderRadius: '10px', 
                                    padding: '10px', 
                                    border: '1px solid #ccc', 
                                    fontSize: '15px' 
                                }} 
                            />
                        </div>
                        <button className='button green' type='submit'>Save Changes</button>
                        <button className='button' type='button' onClick={handleEditToggle}>Cancel</button>
                    </form>
                ) : (
                    <div style={{ textAlign: 'left' }}>
                        <p style={{ fontWeight: 'bold' }}>Company Name: <span style={{ fontWeight: 'normal' }}>{company.company_name}</span></p>
                        <p style={{ fontWeight: 'bold' }}>Company Address: <span style={{ fontWeight: 'normal' }}>{company.company_address}</span></p>
                        <p style={{ fontWeight: 'bold' }}>Contact ID: <span style={{ fontWeight: 'normal' }}>{company.contact_id}</span></p>
                        <div style={{ textAlign: 'center', marginTop: '20px', marginBottom: '20px'}}>
                            <button className='button red' onClick={doDelete}>Delete Company</button>
                            <button className='button green' onClick={handleEditToggle}>Edit Company</button>
                        </div>
                    </div>
                )}
            </div>
        </div>
    );
}

export default Company;
