import { useState } from 'react';

function Company(props) {
    const { company, companies, setCompanies } = props;
    const [expanded, setExpanded] = useState(false);
    const [isEditing, setIsEditing] = useState(false);
    const [editedCompany, setEditedCompany] = useState({ 
        company_name: company.company_name, 
        company_address: company.company_address, 
        contact_id: company.contact_id 
    });

    const expandStyle = {
        display: expanded ? 'block' : 'none'
    };

    async function doDelete(e) {
        e.stopPropagation();
        try {
            const response = await fetch(`http://localhost/api/companies/${company.company_id}`, {
                method: 'DELETE',
            });

            if (response.ok) {
                const newCompanies = companies.filter(c => c.company_id !== company.company_id);
                setCompanies(newCompanies);
            } else {
                console.error('Failed to delete company:', response);
                alert('Failed to delete company. Please try again.');
            }
        } catch (error) {
            console.error('Error during deletion:', error);
            alert('An error occurred during deletion. Please try again.');
        }
    }

    async function doEdit(e) {
        e.stopPropagation();
        try {
            const response = await fetch(`http://localhost/api/companies/${company.company_id}`, {
                method: 'PATCH',
                headers: {
                    'Content-Type': 'application/json',
                },
                body: JSON.stringify(editedCompany),
            });

            if (response.ok) {
                const updatedCompanies = companies.map(c => 
                    c.company_id === company.company_id ? { ...c, ...editedCompany } : c
                );
                setCompanies(updatedCompanies);
                setIsEditing(false); // Exit edit mode
            } else {
                console.error('Failed to update company:', response);
                alert('Failed to update company. Please try again.');
            }
        } catch (error) {
            console.error('Error during update:', error);
            alert('An error occurred during update. Please try again.');
        }
    }

    const handleChange = (e) => {
        const { name, value } = e.target;
        setEditedCompany(prev => ({
            ...prev,
            [name]: value
        }));
    };

    return (
        <div className='company'>
            <div className='title' onClick={() => setExpanded(!expanded)}>
                <h3>{company.company_name}</h3>
            </div>

            <div style={expandStyle}>
                <hr />
                <div style={{ textAlign: 'left' }}>
                    {isEditing ? (
                        <>
                            <input 
                                type="text" 
                                name="company_name" 
                                value={editedCompany.company_name} 
                                onChange={handleChange} 
                            />
                            <input 
                                type="text" 
                                name="company_address" 
                                value={editedCompany.company_address} 
                                onChange={handleChange} 
                            />
                            <input 
                                type="text" 
                                name="contact_id" 
                                value={editedCompany.contact_id} 
                                onChange={handleChange} 
                            />
                        </>
                    ) : (
                        <>
                            <p style={{ fontWeight: 'bold' }}>Company Name: <span style={{ fontWeight: 'normal' }}>{company.company_name}</span></p>
                            <p style={{ fontWeight: 'bold' }}>Company Address: <span style={{ fontWeight: 'normal' }}>{company.company_address}</span></p>
                            <p style={{ fontWeight: 'bold' }}>Contact ID: <span style={{ fontWeight: 'normal' }}>{company.contact_id}</span></p>
                        </>
                    )}
                </div>

                <div style={{ textAlign: 'center', marginTop: '20px', marginBottom: '20px'}}>
                    {isEditing ? (
                        <div>
                            <button className='button green' onClick={doEdit}>Save Changes</button>
                            <button className='button' onClick={() => setIsEditing(false)}>Cancel</button>
                        </div>
                    ) : (
                        <button className='button' onClick={() => setIsEditing(true)}>Edit Company</button>
                    )}
                    <button className='button red' onClick={doDelete}>Delete Company</button>
                </div>
            </div>
        </div>
    );
}

export default Company;
